    constructor() {
        super();
        this.state = {
            allRecipes: JSON.parse(window.localStorage.getItem("recipeStorage"))
        }
        this.handleAddRecipe = this.handleAddRecipe.bind(this);

    }
    handleAddRecipe(event) {
        event.preventDefault();
        let newDishName = document.getElementById("newDishName").value;
        let newIngredients = document.getElementById("newIngredients").value;
        let newInstructions = document.getElementById("newInstructions").value;
        let newImage = document.getElementById("newImage").value;
        let newRecipe = {
            name: newDishName,
            ingredients: newIngredients.split(","),
            instructions: newInstructions.split("."),
            image: newImage
        }
        let newRecipeList = [];
        for (let i = 0; i < this.state.allRecipes.length; i += 1) {
            newRecipeList.push(this.state.allRecipes[i]);
        }
        newRecipeList.push(newRecipe);
        window.localStorage.setItem("recipeStorage", JSON.stringify(newRecipeList));
        this.setState({
            allRecipes: newRecipeList
        })
    }

    render() {
            return (
                <main>
                    <form onSubmit={this.handleAddRecipe}>
                        <input
                            id="newDishName"
                            placeholder="Dish Name"/>
                        <input
                            id="newImage"
                            placeholder="Image Link"/>
                        <input
                            id="newIngredients"
                            placeholder="Ingredients (separate with comma)" />
                        <input
                            id="newInstructions"
                            placeholder="Instructions (separate with period)" />
                        <button>Finish!</button>
                    </form>
                </main>
            )
        }
}

export default Form;
